public class AuthsRecord{		String serau,au,fn,role,rid,aorder;	public AuthsRecord(){				}			public void setRid(String rd){		rid = rd;		}	public String getRid(){		return rid;		}		public void setSerau(String plaintext_author){		serau = plaintext_author;		}	public String getSerau(){		return serau;		}		public void setAu(String author){		au = author;		}	public String getAu(){		return au;		}		public void setFn(String first_name){		fn = first_name;		}	public String getFn(){		return fn;		}		public void setRole(String rol){		role = rol;		}	public String getRole(){		return role;		}		public void setAorder(String cardinality){		aorder = cardinality;		}	public String getAorder(){		return aorder;		}	}