public class BfvRecord{		String rid,ti,date,jn,jnfk,vo,pg,pub,type,edvo,trans,tt,ts,ed;	public BfvRecord(){				}			public void setRid(String rd){		rid = rd;		}	public String getRid(){		return rid;		}			public void setTi(String title){		ti = title;		}	public String getTi(){		return ti;		}		public void setDate(String dat){		date = dat;		}	public String getDate(){		return date;		}		public void setJn(String journal){		jn = journal;		}	public String getJn(){		return jn;		}		public void setJnfk(String journal_fk){		jnfk = journal_fk;		}	public String getJnfk(){		return jnfk;		}		public void setVo(String volume){		vo = volume;		}	public String getVo(){		return vo;		}		public void setPg(String pages){		pg = pages;		}	public String getPg(){		return pg;		}		public void setPub(String publisher){		pub = publisher;		}	public String getPub(){		return pub;		}			public void setType(String typ){		type = typ;		}	public String getType(){		return type;		}		public void setEdvo(String edited_volume){		edvo = edited_volume;		}	public String getEdvo(){		return edvo;		}		public void setTrans(String tran){		trans = tran;		}	public String getTrans(){		return trans;		}		public void setTt(String trans_title){		tt = trans_title;		}	public String getTt(){		return tt;		}		public void setTs(String trans_s){		ts = trans_s;		}	public String getTs(){		return ts;		}		public void setEd(String edited){		ed = edited;		}			public String getEd(){		return ed;		}		}