import java.lang.*;import java.util.*;import java.io.*;public class QueryCondition{	String boolop;	String field;	String equator;	String value;	public QueryCondition(String bol, String fld, String eqtr, String val){			boolop = bol;		field = fld;		equator = eqtr;		value = val;			}		public String getBoolop(){		return boolop;		}			public String getField(){		return field;		}			public String getEquator(){		return equator;		}			public String getValue(){		return value;		}		}